package vetor;

public class TesteVetor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Cria o vetor do tamanho 4
		
		int[] valores = new int[4];
		
		//Inserir valores em cada posição (indice) do vetor
		
		valores[0] =200;
		valores[1] =10;
		valores[2] =36;
		valores[3] =52;

		//Pegar valores em cada posição (indice) do vator
		
		System.out.println("Posição 0: " + valores[0]);
		System.out.println("Posição 0: " + valores[1]);
		System.out.println("Posição 0: " + valores[2]);
		System.out.println("Posição 0: " + valores[3]);
	}

}
